mapper.select:
select master.id, master.name, master.group_master_uid, master.status, master.content, master.ctime, master.mtime, master.public_flag, master.wx_id_card_url, master.pic_urls, master.video_url, master.start_time, master.end_time, master.service_community_id, good.id as goodId, good.good_name, good.good_description, good.good_pic as goodPic, good.price, good.stock, good.remaining_stock as remainingStock, good.limit_type, good.limit_value from group_purchase_info master left join group_purchase_good good on master.id = good.group_purchase_id
and group_master_uid = #{groupMasterUid}
and name like concat('%', #{groupName})
and status = #{status.value}
and public_flag = #{publicFlag}
order by ctime desc
