mapper.select:
select 
from user_info
where community_id = #{communityId}
  and building_no = #{buildingNo}
  and room_no = #{roomNo}

mapper.update:
update user_info
community_id = #{command.communityId}, building_no = #{command.buildingNo}, room_no = #{command.roomNo}, email = '', temp_email = #{command.email}, nickname = #{command.nickname}, avatar_url = #{command.avatarUrl}, avatar_thumb_url = #{command.avatarThumbUrl}, email_verified = #{command.emailVerified}
where id = #{command.id}

update user_info
set email = #{email},
email_verified = true
where id = #{userId}
and temp_email = #{email}
and email_verified = false
